mapper.update:
update member set
pwd = #{pwd}
where userid = #{userid}

update member set
phone = #{phone}
where userid = #{userid}

update member set
email = #{email}
where userid = #{userid}

mapper.select:
select * from reservation
where userid=#{userid}
order by inday desc

mapper.update:
update reservation
set state = '5'
where id=#{id}

update reservation
set state = ${dDay}
where id=#{id}

update review set
title=#{title}, content=#{content}, star=#{star}, writeday=now()
where rid=#{rid}
